// import Like from './Like';
import React from 'react';

function Review({ reviews, deleteReview}) {

  return reviews.map((review, index) => (
    <div className='poem-row' key={index}>
      <div key={review.id} onClick={() => reviews(review.id)}>
        {review.text}
      </div>
      <div>
        {/* <Like reviewId={review.id}/> */}
        <button
          onClick={() => deleteReview(review.id)} className="delete-btn"
          >Delete</button>
      </div>
    </div>
  ));
};

export default Review;
